mapper.select:
SELECT d.*,e.category
FROM disputes d
LEFT JOIN player_matches pm ON d.play_match_id = pm.id
LEFT JOIN events e ON pm.events_id = e.id


        AND d.create_time >= #{startTime}


        AND d.create_time <= #{endTime}


        AND e.category = #{eventCategory}


        AND d.status = #{status}


ORDER BY d.id DESC
